            if (!aForceUpdate) {
              TelemetryStopwatch.start("FX_TAB_SWITCH_UPDATE_MS");
              window.QueryInterface(Ci.nsIInterfaceRequestor).getInterface(Ci.nsIDOMWindowUtils)
                                                             .beginTabSwitch();
            }

            var oldTab = this.mCurrentTab;

            // Preview mode should not reset the owner
