  this.updateCurrentBrowser(true);


var l = this.mPanelContainer.childNodes.length;
for (var i = 0; i < l; i++) {
  try {
    this.getBrowserAtIndex(i).reload();
  } catch (e) {
    // ignore failure to reload so others will be reloaded
  }
