        (aEvent.screenX >= sourceNode.boxObject.screenX &&
          aEvent.screenX <= (sourceNode.boxObject.screenX +
                             sourceNode.boxObject.width))) {
      return dt.effectAllowed = "none";
    }

    return dt.effectAllowed = "copyMove";
  }
