  switch (targetAnonid) {
    case "scrollbutton-up":
      pixelsToScroll = tabStrip.scrollIncrement * -1;
      break;
    case "scrollbutton-down":
    case "alltabs-button":
    case "newtab-button":
      pixelsToScroll = tabStrip.scrollIncrement;
      break;
