let tabs = this.visibleTabs;

// count backwards for aIndex < 0
if (aIndex < 0)
  aIndex += tabs.length;

if (aIndex >= 0 && aIndex < tabs.length)
  this.selectedTab = tabs[aIndex];

if (aEvent) {
  aEvent.preventDefault();
  aEvent.stopPropagation();
}

property.setter:

// Update the tab
this.mTabBox.selectedTab = val;
return val;

method.body:

