if (tabPos > 0)
  this.moveTabTo(this.mCurrentTab, 0);


var tabPos = this.mCurrentTab._tPos;
if (tabPos < this.browsers.length - 1)
  this.moveTabTo(this.mCurrentTab, this.browsers.length - 1);


