method.body:
let visibleTabs = this.tabbrowser.visibleTabs;
let candidate = visibleTabs[visibleTabs.length - 1];
if (!candidate.selected) {
  this._beforeHoveredTab = candidate;
  candidate.setAttribute("beforehovered", "true");
