this._mouseTargetRect = {
  top:    panelRect.top,
  bottom: panelRect.bottom,
  left:   alignRight ? containerRect.right - panelRect.width : containerRect.left,
  right:  alignRight ? containerRect.right : containerRect.left + panelRect.width
