if (this.action.fallbackType == Ci.nsIObjectLoadingContent.PLUGIN_ACTIVE) {
  if (this.action.pluginPermissionType == Ci.nsIPermissionManager.EXPIRE_SESSION) {
    curState = "allownow";
  }
  else {
    curState = "allowalways";
  }
