  if (reverse && index == -1 || newIndex > maxRow && index != maxRow)
    newIndex = maxRow;
  else if (!reverse && index == -1 || newIndex < 0 && index != 0)
    newIndex = 0;

  if (newIndex < 0 && index == 0 || newIndex > maxRow && index == maxRow)
    newIndex = -1;

  return newIndex;
}

