  handleEvent: function handleEvent(aEvent) {
    if (aEvent.type == "spinup") {
      if (this.widget.month == 0)
        this.widget.year++;
    } else if (this.widget.month == 11) {
      this.widget.year--;
    }
  },
  widget: this
};
