      MOZ_TRY_VAR(result, parseInterfaceBindingWithInitializer(start, context));
      break;
    case BinASTKind::ObjectBinding:
      MOZ_TRY_VAR(result, parseInterfaceObjectBinding(start, context));
      break;
    default:
