  let children = this._children;
  let title = this._bundle.GetStringFromName("editBookmarkNewFolder");
  let newId = PlacesUtils.bookmarks.createFolder(parent.itemId, title, PlacesUtils.bookmarks.DEFAULT_INDEX);
