// we don't require the leading space (after each comma)
var tags = this.tags.split(",");
for (var i = 0; i < tags.length; i++) {
  // remove trailing and leading spaces
  tags[i] = tags[i].trim();

  // remove empty entries from the array.
  if (tags[i] == "") {
    tags.splice(i, 1);
