  popup = document.createElement("popup");
  popup.setAttribute("type", "autocomplete");
  popup.setAttribute("hidden", "true");

  var popupset = document.getAnonymousElementByAttribute(this, "anonid", "popupset");
  popupset.appendChild(popup);
}
popup.mInput = this;
popup;

