method.body:

// detect the desired height of the tree
var bx = this.tree.treeBoxObject;
var view = this.tree.view;
if (!view)
  return;
var rows = this.maxRows;
if (!view.rowCount || (rows && view.rowCount < rows))
  rows = view.rowCount;

