  this.webNavigation.loadURI(aURI, aFlags, aReferrerURI, aPostData, null);
} finally {
  if (this.userTypedClear)
    this.userTypedClear--;
}


try {
  this.loadURI(this.homePage);
}
catch (e) {
}

