  while (this.selectedItems.length > 0) {
    let item = this.selectedItems[0];
    item.selected = false;
    this.selectedItems.remove(item);
  }
}

this._selectionStart = null;
this._fireOnSelect();


var suppress = this._suppressOnSelect;
if (aTimeout != -1)
  this._suppressOnSelect = true;

