  this._userSelecting = true;
  this._mayReverse = true;
  this.moveByOffset(aOffset, !aEvent.ctrlKey, aEvent.shiftKey);
  this._userSelecting = false;
  this._mayReverse = false;
  aEvent.preventDefault();
