const XULNS =
  "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";

var item = this.ownerDocument.createElementNS(XULNS, "listitem");
item.setAttribute("label", aLabel);
item.setAttribute("value", aValue);
