this.adjustArrowPosition();

// set fading
var fade = this.getAttribute("fade");
var fadeDelay = (fade == "fast") ? 1 : fade == "slow" ? 4000 : 0;
if (fadeDelay) {
  this._fadeTimer = setTimeout(function (self) {
    self.style.opacity = 0.2;
  }, fadeDelay, this);
}

