var k = aEvent.keyCode;
if (k == KeyEvent.DOM_VK_TAB ||
    k == KeyEvent.DOM_VK_UP || k == KeyEvent.DOM_VK_DOWN ||
    k == KeyEvent.DOM_VK_PAGE_UP || k == KeyEvent.DOM_VK_PAGE_DOWN)
