handlers.handler:

if (this.selectedItems.length < 1)
  return;
this._isUpSelection=0;
this._isDownSelection=0;
var n = this.getPreviousItem(this.selectedItems[this.selectedItems.length-1], 1);
if (n) {
  this.ensureIndexIsVisible(this.getIndexOfItem(n));
