var lastCol = null;
var currentX = this.boxObject.x;
while (col) {
  if (col.localName == "outlinercol" && col.parentNode.parentNode == this) {
    var cw = col.boxObject.width;
    if (cw > 0) {
      currentX += cw;
      if (currentX - (cw*aThresh) > aX)
        return col;
    }
    lastCol = col;
  }
  col = col.boxObject.nextSibling;
}

if (aPos) aPos.value = "after";
return lastCol;

