radio.setAttribute("label", label);
radio.setAttribute("value", value);
this.appendChild(radio);


var XULNS = "http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul";
var radio = document.createElementNS(XULNS, "radio");
radio.setAttribute("label", label);
radio.setAttribute("value", value);
