} catch (e) {}
if (!event.ctrlKey)
  this.view.selection.timedSelect(c+1, this._selectDelay);
else // Ctrl+Down moves the anchor without selecting
  this.currentIndex = c + 1;
this.treeBoxObject.ensureRowIsVisible(c+1);


if (this.view.selection.single)
